export default function Button({
  variant = 'primary',
  size = 'sm',
  children,
  onClick,
  disabled = false,
  className = ''
}: ButtonProps) {
  const baseStyles = 'inline-flex items-center justify-center rounded-md font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:pointer-events-none';

  const variantStyles = {
    primary: 'bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-500',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 focus:ring-gray-500',
    outline: 'border border-gray-300 bg-white text-gray-700 hover:bg-gray-50 focus:ring-blue-500',
    ghost: 'text-gray-600 hover:bg-gray-100 focus:ring-gray-500'
  };

  const sizeStyles = {
    sm: 'px-3 py-2 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-6 py-3 text-lg'
  };

  const combinedClassName = `${baseStyles} ${variantStyles[variant]} ${sizeStyles[size]} ${className}`.trim();

  return (
    <button
      className={combinedClassName}
      onClick={onClick}
      disabled={disabled}
      data-testid="rendered-component-button"
    >
      {typeof children === 'function' ? children() : children}
    </button>
  );
}

import React from 'react';

interface InputProps {
  type?: 'text' | 'email' | 'password' | 'number' | 'tel' | 'url';
  name?: string;
  placeholder?: string;
  value?: string;
  defaultValue?: string;
  onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onFocus?: (event: React.FocusEvent<HTMLInputElement>) => void;
  onBlur?: (event: React.FocusEvent<HTMLInputElement>) => void;
  disabled?: boolean;
  required?: boolean;
  size?: 'sm' | 'md' | 'lg';
  variant?: 'default' | 'error' | 'success';
  className?: string;
  label?: string;
  helperText?: string;
  errorMessage?: string;
}

export default function Input({
  type = 'text',
  name,
  placeholder,
  value,
  defaultValue,
  onChange,
  onFocus,
  onBlur,
  disabled = false,
  required = false,
  size = 'md',
  variant = 'default',
  className = '',
  label,
  helperText,
  errorMessage
}: InputProps) {
  const baseStyles = 'border rounded-md transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:pointer-events-none';

  const variantStyles = {
    default: 'border-gray-300 focus:border-blue-500 focus:ring-blue-500',
    error: 'border-red-500 focus:border-red-500 focus:ring-red-500',
    success: 'border-green-500 focus:border-green-500 focus:ring-green-500'
  };

  const sizeStyles = {
    sm: 'px-3 py-2 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-4 py-3 text-lg'
  };

  const inputClassName = `${baseStyles} ${variantStyles[variant]} ${sizeStyles[size]} ${className}`.trim();

  const inputId = React.useId();

  return (
    <div className="space-y-1">
      {label && (
        <label htmlFor={inputId} className="block text-sm font-medium text-gray-700">
          {label}
          {required && <span className="text-red-500 ml-1">*</span>}
        </label>
      )}

      <input
        id={inputId}
        name={name}
        type={type}
        placeholder={placeholder}
        value={value}
        defaultValue={defaultValue}
        onChange={onChange}
        onFocus={onFocus}
        onBlur={onBlur}
        disabled={disabled}
        required={required}
        className={inputClassName}
      />

      {(helperText || errorMessage) && (
        <p className={`text-sm ${errorMessage ? 'text-red-600' : 'text-gray-500'}`}>
          {errorMessage || helperText}
        </p>
      )}
    </div>
  );
}

import React from 'react';

interface CardProps {
  children: React.ReactNode | (() => React.ReactNode);
  className?: string;
  padding?: 'none' | 'sm' | 'md' | 'lg';
  shadow?: 'none' | 'sm' | 'md' | 'lg';
  border?: boolean;
  rounded?: 'none' | 'sm' | 'md' | 'lg';
  header?: React.ReactNode | (() => React.ReactNode);
  footer?: React.ReactNode | (() => React.ReactNode);
}

export default function Card({
  children,
  className = '',
  padding = 'md',
  shadow = 'md',
  border = true,
  rounded = 'md',
  header,
  footer
}: CardProps) {
  const baseStyles = 'bg-white';

  const paddingStyles = {
    none: '',
    sm: 'p-3',
    md: 'p-6',
    lg: 'p-8'
  };

  const shadowStyles = {
    none: '',
    sm: 'shadow-sm',
    md: 'shadow-md',
    lg: 'shadow-lg'
  };

  const roundedStyles = {
    none: '',
    sm: 'rounded-sm',
    md: 'rounded-lg',
    lg: 'rounded-xl'
  };

  const borderStyle = border ? 'border border-gray-200' : '';

  const cardClassName = `${baseStyles} ${shadowStyles[shadow]} ${roundedStyles[rounded]} ${borderStyle} ${className}`.trim();

  const contentPadding = header || footer ? 'none' : padding;
  const headerFooterPadding = paddingStyles[padding];
  const bodyPadding = paddingStyles[contentPadding];

  return (
    <div className={cardClassName}>
      {header && (
        <div className={`border-b border-gray-200 ${headerFooterPadding}`}>
          {typeof header === 'function' ? header() : header}
        </div>
      )}

      <div className={header || footer ? headerFooterPadding : bodyPadding}>
        {typeof children === 'function' ? children() : children}
      </div>

      {footer && (
        <div className={`border-t border-gray-200 ${headerFooterPadding}`}>
          {typeof footer === 'function' ? footer() : footer}
        </div>
      )}
    </div>
  );
}

"use client"

import {
  ColumnDef,
  ColumnFiltersState,
  SortingState,
  RowSelectionState,
  VisibilityState,
  flexRender,
  getCoreRowModel,
  getFacetedRowModel,
  getFacetedUniqueValues,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
  PaginationState,
  Updater
} from "@tanstack/react-table"
import {
  Table as UITable,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"
import { DataTablePagination } from "../../../DataTablePagination"
import { DataTableToolbar } from "./_components/data-table-toolbar"
import { Filter } from "@/lib/interfaces"
import I18nComponent from "@/components/ui/i18n-component"

import { useCallback, useEffect, useState, useRef } from "react"

/**
 * Props interface for the DataTable component
 */
interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[]     // Column definitions
  data: TData[]                           // Table data
  tableLabel: string                      // Label for the table
  filters?: Filter[]                      // Array of filter configurations  
  createToolbarButtons?: (               // Optional function to create toolbar buttons
    rowSelection?: RowSelectionState, 
    setRowSelection?: React.Dispatch<React.SetStateAction<RowSelectionState>>, 
    hasSelected?: boolean
  ) => React.JSX.Element
  onRowClick?: (id: string) => void      // Optional row click handler
  identifier?: string                     // Optional identifier field
  initialSorting?: SortingState          // Optional initial sorting state
}

/**
 * DataTable Component
 * A reusable data table component with sorting, filtering, and pagination capabilities
 */
export function DataTable<TData, TValue>({
  columns,
  data,
  tableLabel,
  filters = [],
  onRowClick,
  createToolbarButtons,
  identifier = "name",
  initialSorting = []
}: DataTableProps<TData, TValue>) {

  // Validation check
  if (!columns || !Array.isArray(columns)) {
    console.error('‚ùå DataTable: columns prop is invalid:', columns);
    return (
      <div className="border-2 border-dashed border-red-300 rounded-lg p-8">
        <div className="text-center">
          <h3 className="text-lg font-semibold text-red-700 mb-2">
            Invalid Columns
          </h3>
          <p className="text-red-600 mb-4">
            The columns prop is {typeof columns}. Expected an array of column definitions.
          </p>
          <pre className="text-xs bg-red-50 p-4 rounded">
            {JSON.stringify(columns, null, 2)}
          </pre>
        </div>
      </div>
    );
  }

  // State management for table features
  const [rowSelection, setRowSelection] = useState<RowSelectionState>({})
  const [columnVisibility, setColumnVisibility] = useState<VisibilityState>({})
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([])
  const [sorting, setSorting] = useState<SortingState>(initialSorting)

  // Pagination state
  const [pageIndex, setPageIndex] = useState(0)
  const [pageSize, setPageSize] = useState(20)

  // Refs for pagination handling
  const lastValidPageIndexRef = useRef(0)
  const isDataChangingRef = useRef(false)
  const firstTryRef = useRef(true)

  console.log("filters", filters);
  console.log("sorting", sorting);
  console.log("columnFilters", columnFilters);
  console.log("columnVisibility", columnVisibility);
  console.log("columns", columns);
  console.log("data", data);


  /**
   * Handles pagination state changes
   * Manages edge cases when data changes and pagination needs to be reset
   */
  const handlePaginationChange = useCallback((updater: Updater<PaginationState>) => {
    firstTryRef.current = false

    if (isDataChangingRef.current) {
      isDataChangingRef.current = false
      return
    }

    if (typeof updater === 'function') {
      const newState = updater({ pageIndex, pageSize })
      setPageSize(newState.pageSize)
      setPageIndex(newState.pageIndex)
      lastValidPageIndexRef.current = newState.pageIndex
    } else {
      setPageSize(updater.pageSize)
      setPageIndex(updater.pageIndex)
      lastValidPageIndexRef.current = updater.pageIndex
    }
  }, [pageIndex, pageSize])

  // Initialize table instance with all features
  const table = useReactTable({
    data,
    columns,
    state: {
      sorting,
      columnVisibility,
      rowSelection,
      columnFilters,
      pagination: { pageIndex, pageSize },
    },
    onPaginationChange: handlePaginationChange,
    enableRowSelection: true,
    onRowSelectionChange: setRowSelection,
    onSortingChange: setSorting,
    onColumnFiltersChange: setColumnFilters,
    onColumnVisibilityChange: setColumnVisibility,
    // Feature getters
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFacetedRowModel: getFacetedRowModel(),
    getFacetedUniqueValues: getFacetedUniqueValues(),
  })

  // Reset pagination when data changes
  useEffect(() => {
    if(!firstTryRef.current) {
      isDataChangingRef.current = true
    }
    setPageIndex(lastValidPageIndexRef.current)
  }, [data])

  // Default toolbar buttons implementation
  const defaultCreateToolbarButtons = useCallback(() => {
    return <div className="flex gap-2">
      <button className="px-3 py-1 text-sm border rounded hover:bg-gray-50">
        Add Item
      </button>
    </div>
  }, []);

  // Memoized toolbar buttons creation
  const toolBarButtonsProcessed = useCallback(() => {
    const buttonsCreator = createToolbarButtons || defaultCreateToolbarButtons;
    return buttonsCreator(
      rowSelection, 
      setRowSelection, 
      table.getIsSomeRowsSelected() || table.getIsAllRowsSelected()
    )
  }, [createToolbarButtons, defaultCreateToolbarButtons, setRowSelection, rowSelection, table])

  return (
    <div className="flex flex-col h-fit min-w-[400px] space-y-4">
      {/* Toolbar with filters and custom buttons */}
      <DataTableToolbar 
        identifier={identifier} 
        table={table} 
        tableLabel={tableLabel} 
        filters={filters} 
        toolBarButtonsProcessed={toolBarButtonsProcessed}
      />
      {/* Main table container */}
      <div className="relative flex-grow overflow-auto border rounded-md">
        <UITable>
          {/* Table header */}
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  return (
                    <TableHead key={header.id} colSpan={header.colSpan}>
                      {header.isPlaceholder
                        ? null
                        : flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                    </TableHead>
                  )
                })}
              </TableRow>
            ))}
          </TableHeader>
          {/* Table body */}
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() && "selected"}
                  onClick={() => onRowClick?.(row.getValue("id"))}
                >
                  {row.getVisibleCells().map((cell) => (
                    <TableCell key={cell.id} >
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              // No results message
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="h-24 text-center"
                >
                  <I18nComponent i18nKey="ui:noResults" />
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </UITable>
      </div>
      {/* Pagination controls */}
      <DataTablePagination table={table} />
    </div>
  )
}
